import React from 'react';
import {
  Box,
  Typography,
  Paper,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  List,
  ListItem,
  ListItemText,
  Chip,
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { JOBS } from '../constants';
import { WorkflowNode } from '../types';

interface JobsPanelProps {
  onNodeAdd: (node: WorkflowNode) => void;
}

const JobsPanel: React.FC<JobsPanelProps> = ({ onNodeAdd }) => {
  const handleTaskDrop = (event: React.DragEvent, taskId: string) => {
    event.preventDefault();
    const apiId = event.dataTransfer.getData('application/reactflow');

    if (apiId) {
      // Create a new node when API is dropped on a task
      const newNode: WorkflowNode = {
        id: `${taskId}-${apiId}-${Date.now()}`,
        type: 'api',
        data: {
          label: `API on ${taskId}`,
          // We'll populate this with actual API data later
        },
        position: { x: Math.random() * 500, y: Math.random() * 300 },
      };

      onNodeAdd(newNode);
    }
  };

  const handleDragOver = (event: React.DragEvent) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  };

  return (
    <Box
      sx={{
        width: 350,
        height: '100vh',
        backgroundColor: 'white',
        borderLeft: '1px solid #e0e0e0',
        overflow: 'auto',
        pt: 8, // Account for header
      }}
    >
      <Box sx={{ p: 2 }}>
        <Typography variant="h6" gutterBottom>
          Workflow Jobs
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
          Drop APIs onto tasks to create workflow steps
        </Typography>

        {JOBS.map((job, index) => (
          <Accordion key={job.id} defaultExpanded={index === 0}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}>
              <Box>
                <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                  {job.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {job.description}
                </Typography>
              </Box>
            </AccordionSummary>
            <AccordionDetails>
              <List dense>
                {job.tasks.map((task) => (
                  <ListItem
                    key={task.id}
                    sx={{
                      border: '2px dashed #e0e0e0',
                      borderRadius: 1,
                      mb: 1,
                      minHeight: 60,
                      '&:hover': {
                        borderColor: '#1976d2',
                        backgroundColor: '#f8f9ff',
                      },
                    }}
                    onDrop={(e) => handleTaskDrop(e, task.id)}
                    onDragOver={handleDragOver}
                  >
                    <ListItemText
                      primary={
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          <Typography variant="body2" sx={{ fontWeight: 'medium' }}>
                            {task.name}
                          </Typography>
                          <Chip label="Drop Zone" size="small" variant="outlined" />
                        </Box>
                      }
                      secondary={task.description}
                    />
                  </ListItem>
                ))}
              </List>
            </AccordionDetails>
          </Accordion>
        ))}
      </Box>
    </Box>
  );
};

export default JobsPanel;
