import React from 'react';
import {
  Box,
  Typography,
  Paper,
  List,
  ListItem,
  ListItemText,
  Chip,
  Divider,
} from '@mui/material';
import { DUMMY_APIS, SMFG_APIS } from '../constants';

interface ApiSidebarProps {
  lenderName: string;
}

const ApiSidebar: React.FC<ApiSidebarProps> = ({ lenderName }) => {
  const onDragStart = (event: React.DragEvent, api: any) => {
    console.log('Dragging API:', api);
    // Store the API data as JSON in the dataTransfer with complete information
    event.dataTransfer.setData('application/reactflow', JSON.stringify({
      id: `api-${Date.now()}`,
      type: 'api',
      data: {
        label: api.name,
        name: api.name,
        description: api.description || '',
        api: {
          name: api.name,
          description: api.description || '',
          category: api.category || '',
          responses: api.responses || []
        },
        isApi: true
      }
    }));
    event.dataTransfer.effectAllowed = 'move';
  };

  // Select the appropriate API list based on the lender name
  const apiList = lenderName === 'SMFG' ? SMFG_APIS : DUMMY_APIS;

  // Group APIs by category
  const apisByCategory = apiList.reduce((acc, api) => {
    if (!acc[api.category]) {
      acc[api.category] = [];
    }
    acc[api.category].push(api);
    return acc;
  }, {} as Record<string, typeof apiList>);

  return (
    <Box
      sx={{
        width: 300,
        height: '100vh',
        backgroundColor: 'white',
        borderRight: '1px solid #e0e0e0',
        overflow: 'auto',
        pt: 8, // Account for header
      }}
    >
      <Box sx={{ p: 2 }}>
        <Typography variant="h6" gutterBottom>
          Available APIs
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
          Drag and drop APIs to create your workflow
        </Typography>

        {Object.entries(apisByCategory).map(([category, apis]) => (
          <Box key={category} sx={{ mb: 3 }}>
            <Typography variant="subtitle1" sx={{ mb: 1, fontWeight: 'bold' }}>
              {category}
            </Typography>
            <List dense>
              {apis.map((api) => (
                <ListItem
                  key={api.id}
                  sx={{
                    border: '1px solid #e0e0e0',
                    borderRadius: 1,
                    mb: 1,
                    cursor: 'grab',
                    '&:hover': {
                      backgroundColor: '#f5f5f5',
                      borderColor: '#1976d2',
                    },
                    '&:active': {
                      cursor: 'grabbing',
                    },
                  }}
                  draggable
                  onDragStart={(e) => onDragStart(e, api)}
                >
                  <ListItemText
                    primary={api.name}
                    secondary={
                      <Box>
                        <Typography variant="body2" sx={{ mb: 1 }}>
                          {api.description}
                        </Typography>
                        {api.responses && (
                          <Chip
                            label={`${api.responses.length} responses`}
                            size="small"
                            variant="outlined"
                          />
                        )}
                      </Box>
                    }
                  />
                </ListItem>
              ))}
            </List>
            {category !== Object.keys(apisByCategory)[Object.keys(apisByCategory).length - 1] && (
              <Divider sx={{ my: 2 }} />
            )}
          </Box>
        ))}
      </Box>
    </Box>
  );
};

export default ApiSidebar;
